function left(){
    if(index == 0){
        index++
    }

    index--;
    display()
}

function right(){
    if(index == articleList.length - 1){
        index--
    }

    index++;
    display()
}

function display(){
    document.getElementById("title").innerHTML = articleList[index][0]
    document.getElementById("datebar").innerHTML = articleList[index][1]
    document.getElementById("article").innerHTML = articleList[index][2]
}

